import React, {useEffect, useState} from "react";

function App() {
    const [email, setEmail] = useState('')
    const [pass, setPass] = useState('')
    const [emailDirty, setEmailDirty] = useState(false)
    const [passDirty, setPassDirty] = useState(false)
    const [emailError, setEmailError] = useState('Почта не может быть пустой!')
    const [passError, setPassError] = useState('Пароль не может быть пустым!')
    const [formValid, setFormValid] = useState(false)

    useEffect(() => {
        if (emailError || passError) {
            setFormValid(false)
        } else {
            setFormValid(true)
        }
    }, [emailError, passError])

    const emailHandler = (e) => {
        setEmail(e.target.value)
        const re =
            /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (!re.test(String(e.target.value))) {
            setEmailError('Некоректная почта')
        } else {
            setEmailError('')
        }
    }

    const passHandler = (e) => {
        setPass(e.target.value)
        if (e.target.value.length < 5) {
            setPassError('Пароль должен быть длиннее 5 сиволов')
            if (!e.target.value) {
                setPassError('Пароль не может быть пустым')
            }
        } else {
            setPassError('')
        }
    }

    const blurHandler = (e) => {
        switch (e.target.name) {
            case 'email':
                setEmailDirty(true)
                break
            case 'pass':
                setPassDirty(true)
                break
        }
    }

    return (
        <div className="App">
            <form>
                <h1>Вход</h1>
                {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
                <input onChange={e => emailHandler(e)} value={email} onBlur={e => blurHandler(e)} name="email" type="text" placeholder="Почта"/>
                {(passDirty && passError) && <div style={{color: 'red'}}>{passError}</div>}
                <input onChange={e => passHandler(e)} value={pass} onBlur={e => blurHandler(e)} name="password" type="password" placeholder="Пароль"/>
                <button disabled={!formValid} type="submit">Вход</button>
            </form>
        </div>
  );
}

export default App;
